      my @parts = split(/\./,$entry);
      my $nParts = scalar(@parts);
      my $basename = '';
      my $extension = 'noExt';
      my $uncompressedExtension = 'noExt';
      my $isCompressed = 0;
      if ( $nParts == 1) {
        $basename = $entry;
      } else {
        $extension = $parts[$nParts-1];
        my %compressedExtensions = ( gz=>1, zip=>1, bz2=>1 );
        if ( $compressedExtensions{$extension} ) {
          $isCompressed = 1;
          if ( $nParts == 2 ) {
            $basename = $parts[0];
          } else {
            $uncompressedExtension = $parts[$nParts-2];
            $basename = join(".",@parts[0..$nParts-3]);
          }
        } else {
          $uncompressedExtension = $extension;
          $basename = join(".",@parts[0..$nParts-2]);
        }
      }

      #### special handling for files that seem to be dates as extensions
      if ( $uncompressedExtension =~ /^[\d\-]+$/ ) {
	$uncompressedExtension = "date";
      }

      my $tracking = { fileTag=>$fileTag, filePath=>$filePath, filename=>$entry, dataDirectory=>$dataDirectory, dataDirectoryId=>$dataDirectoryId };
      my ($dev,$ino,$mode,$nlink,$uid,$gid,$rdev,$size,$atime,$mtime,$ctime,$blksize,$blocks) = stat($filePath);
      $size = 0 if ( ! defined($size) );
      $mtime = 0 if ( ! defined($mtime) );
      $mode = 0 if ( ! defined($mode) );
      $stats{totalSize} += $size;

      #### Test the file to make sure it can be opened
      my $isReadable = 0;
      if ( open(TESTFILE,$filePath) ) {
	$isReadable = 1;
	close(TESTFILE);
      }

      if ( exists($qckb->{files}->{$fileTag}) ) {

	#### If this file has already been previously processed, then remove the isNew flag
	my $signatures = $qckb->{files}->{$fileTag}->{signatures};
	if ( $signatures->{tracking}->{hasSignatures} && $signatures->{tracking}->{hasBeenCollated} ) {
          $tracking->{isNew} = 0;
          $qckb->{files}->{$fileTag}->{signatures}->{tracking}->{isNew} = 0;
        }

        if ( $mtime ne $qckb->{files}->{$fileTag}->{signatures}->{extrinsic}->{mtime} ) {
          $qckb->{files}->{$fileTag} ->{signatures}->{tracking}->{isChanged} = 1;
        }
      } else {
        $tracking->{isNew} = 1;
        $stats{newFiles}++;
        $qckb->{files}->{$fileTag} ->{signatures}->{tracking} = $tracking;
        my $extrinsic = { filename=>$entry, mtime=>$mtime, size=>$size, mode=>$mode, extension=>$extension, iscompressed=>$isCompressed, uncompressedExtension=>$uncompressedExtension, basename=>$basename, isReadable=>$isReadable };
        $qckb->{files}->{$fileTag}->{signatures}->{extrinsic} = $extrinsic;
      }

    }
  }

  #### Convert totalSize to friendlier units
  my $totalSizeStr = "$stats{totalSize} bytes";
  $totalSizeStr = int($stats{totalSize}/1024+0.5)." KiB" if ( $stats{totalSize}/1024 > 5 );
  $totalSizeStr = int($stats{totalSize}/1024/1024+0.5)." MiB" if ( $stats{totalSize}/1024/1024 > 3 );
  $totalSizeStr = int($stats{totalSize}/1024/1024/1024+0.5)." GiB" if ( $stats{totalSize}/1024/1024/1024 > 3 );


  $response->{stats} = \%stats;
  $response->logEvent( level=>'INFO', minimumVerbosity=>0, message=>"$stats{totalFiles} files ($totalSizeStr) scanned. $stats{newFiles} files are new to this scan.", verbose=>$verbose, debug=>$debug, quiet=>$quiet, outputDestination=>$outputDestination );

  #### Create an entry in the updates log about what this did
  my $comment;
  if ( $dataDirectory ) {
    $comment = "Scanned data directory '$dataDirectory' and added $stats{newFiles} new files";
  } else {
    $comment = "Scanned files contained in '$inputFiles' and added $stats{newFiles} new files";
  }

  my ($sec,$min,$hour,$mday,$mon,$year) = localtime();
  my $updateEntry = { datetime=>sprintf("%d-%d-%d %d:%d:%d",1900+$year,$mon+1,$mday,$hour,$min,$sec),
    operation => $METHOD,
    comment => $comment
  };
  push(@{$qckb->{updates}},$updateEntry);

  #### END CUSTOMIZATION. DO NOT EDIT MANUALLY BELOW THIS. EDIT MANUALLY ONLY ABOVE THIS.
  {
  if ( ! $isImplemented ) {
    $response->logEvent( status=>'ERROR', level=>'ERROR', errorCode=>"Method${METHOD}NotImplemented", message=>"Method $METHOD has not yet be implemented", verbose=>$verbose, debug=>$debug, quiet=>$quiet, outputDestination=>$outputDestination );
  }

  #### Update the status codes and return
  $response->setState( status=>'OK', message=>"Method $METHOD completed normally") if ( $response->{status} eq 'NOTSET' );
  print "DEBUG: Exiting $CLASS.$METHOD\n" if ( $debug );
  }
  return $response;
}


sub show {
###############################################################################
# show
###############################################################################
  my $METHOD = 'show';
  print "DEBUG: Entering $CLASS.$METHOD\n" if ( $DEBUG );
  my $self = shift || die ("self not passed");
  my %parameters = @_;

  #### Create a simple text representation of the data in the object
  my $buffer = '';
  $buffer .= "$self\n";
  my $dataDirectory = $self->getDataDirectory() || '';
  $buffer .= "  dataDirectory=$dataDirectory\n";
  my $isChanged = $self->getIsChanged() || '';
  $buffer .= "  isChanged=$isChanged\n";
  my $kbRootPath = $self->getKbRootPath() || '';
  $buffer .= "  kbRootPath=$kbRootPath\n";
  my $qckb = $self->getQckb() || '';
  $buffer .= "  qckb=$qckb\n";

  print "DEBUG: Exiting $CLASS.$METHOD\n" if ( $DEBUG );
  return $buffer;
}


sub flattenAttributes {
###############################################################################
# flattenAttributes
# Reduce the depth of attributes to the top level, except permit hashes and
# arrays of scalars
###############################################################################
  my ($attributeName,$attributeValue) = @_;

  my $result;

  #### If the passed attributeValue is just a scalar, then record it as is (probably should never happen?)
  if ( ref($attributeValue) eq '' || ref($attributeValue) eq 'JSON::PP::Boolean' ) {
    $result->{attributeName} = $attributeValue;

  #### Else if this is a HASH then process it
  } elsif ( ref($attributeValue) eq 'HASH' ) {

    #### First loop through to see if they're all scalars or not
    my $allAreScalars = 1;
    foreach my $key ( keys(%{$attributeValue}) ) {
      my $value = $attributeValue->{$key};
      if ( ref($value) ne '' && ref($value) ne 'JSON::PP::Boolean' ) {
        $allAreScalars = 0;
      }
    }
      
    #### If they're all scalars, then return this as a histogram as is
    if ( $allAreScalars ) {
      $result->{$attributeName} = $attributeValue;

    #### But if at least one is complex, then record the scalars as attributes and recurse into the complex ones
    } else {
      foreach my $key ( keys(%{$attributeValue}) ) {
        my $value = $attributeValue->{$key};
        if ( ref($value) eq '' || ref($value) eq 'JSON::PP::Boolean' ) {
          $result->{"$attributeName.$key"} = $value;
        } else {
          my $recursedResult = flattenAttributes("$attributeName.$key",$value);
          foreach my $recursedKey ( keys(%{$recursedResult}) ) {
            $result->{$recursedKey} = $recursedResult->{$recursedKey};
          }
        }
      }
    }

  #### Else if this is an ARRAY then process it
  } elsif ( ref($attributeValue) eq 'ARRAY' ) {

    #### First loop through to see if they're all scalars or not
    my $allAreScalars = 1;
    foreach my $value ( @{$attributeValue} ) {
      if ( ref($value) ne '' && ref($value) ne 'JSON::PP::Boolean' ) {
        $allAreScalars = 0;
      }
    }
      
    #### If they're all scalars, then return this as a histogram as is
    if ( $allAreScalars ) {
      $result->{$attributeName} = $attributeValue;

    #### But if at least one is complex, then record the scalars as attributes and recurse into the complex ones
    } else {
      my $i = 0;
      foreach my $value ( @{$attributeValue} ) {
        if ( ref($value) eq '' || ref($value) eq 'JSON::PP::Boolean' ) {
          $result->{"$attributeName.$i"} = $value;
        } else {
          my $recursedResult = flattenAttributes("$attributeName.$i",$value);
          foreach my $recursedKey ( keys(%{$recursedResult}) ) {
            $result->{$recursedKey} = $recursedResult->{$recursedKey};
          }
        }
        $i++;
      }
    }

  }
  
  return $result;
}


sub recastTransitionHistogram {
###############################################################################
# recastTransitionHistogram
# Transform the special transition_histogram tag from a 3x3 matrix into a
# real histogram
###############################################################################
  my ($matrix) = @_;
  my $result;

  #### New format
  if ( ref($matrix) eq 'HASH' ) {
    my @states = ( "lf", "cr", "oc" );
    foreach my $i ( @states ) {
      foreach my $j ( @states ) {
	$result->{"$i-$j"} = $matrix->{$i}->{$j};
      }
    }
    return $result;
  }

  #### Old format
  my @states = ( "LF", "CR", "CHAR" );
  foreach my $i ( 0..$#states ) {
    foreach my $j ( 0..$#states ) {
      $result->{"$states[$i]-$states[$j]"} = $matrix->[$i]->[$j];
    }
  }

  return $result;
}


###############################################################################
# parseModels
sub parseModels {
  my $self = shift || die ("Must be called as an object method");
  my %opts = @_;
  die unless $opts{kb};

  my $qckb = $opts{kb}->{_qckb};
  my $sens = $opts{sens} || 'all';
  my %models;

  # Loop over data structure.  File type
  for my $ft ( keys( %{$qckb->{fileTypes}} ) ) {
    $models{$ft} = {};

    my @files;
    my @ftags;
    # Under type read list of files
    for my $file ( @{$qckb->{fileTypes}->{$ft}->{fileTagList}} ) {
      push @files, basename( $file );
      if ( $opts{nidx} == 1 ) {
        push @ftags, basename( $file );
      } else {
        my @path = File::Spec->splitdir( $file );
        my $idx = ( $opts{nidx} )  * -1;
        push @ftags, $path[$idx];
      }
    }
    $models{$ft}->{files} = \@files;

    # Then Signature
    for my $sig ( keys( %{$qckb->{fileTypes}->{$ft}->{signatures}} ) ) {
      my $sig_obj = $qckb->{fileTypes}->{$ft}->{signatures}->{$sig};

      # Then individual 'Elements'
      for my $sigelem ( keys( %{$sig_obj} ) ) {
        my $has_outliers = 0;
        my $has_deviations = 0;
        my @dev;

        next unless $sig_obj->{$sigelem}->{model}->{deviations};

        my $fidx = 0;
        for my $dev ( @{$sig_obj->{$sigelem}->{model}->{deviations}} ) {
#        use Data::Dumper;
#        die Dumper( $dev ) if $sigelem =~ /columns.1.label/;
#          next unless defined ( $dev->{deviationFlag} );
          $dev->{deviationFlag} ||= 'normal';
          if ( $dev->{deviationFlag} =~ /extremity/i ) {
            $has_deviations++;
          } elsif ( $dev->{deviationFlag} =~ /outlier/i ) {
            $has_deviations++;
            $has_outliers++;
          }
          $dev->{filename} = $files[$fidx];
          $dev->{filetag} = $ftags[$fidx];
          push @dev, $dev;
          $fidx++;
        }

        my $mkey = $sig . '.' . $sigelem;
#        $mkey =~ s/FileSignature::/FS/;

        # desired sensitivity
        if ( $sens eq 'outliers' ) {
          next unless $has_outliers;
        } elsif ( $sens eq 'deviations' ) {
          next unless $has_deviations;
        }


        $models{$ft}->{$mkey} = { hasout => $has_outliers,
                                    data => \@dev };
      }
    }
  }
#  use Data::Dumper;
#  print Dumper( $models{out}->{'bdqc.builtin.tabular.tabledata.columns.1.labels'} );
  return \%models;
}


sub splitFilePath {
###############################################################################
# splitFilePath
###############################################################################
  my $METHOD = 'splitFilePath';
  print "DEBUG: Entering $CLASS.$METHOD\n" if ( $DEBUG );
  my $self = shift || die ("self not passed");
  my $filePath = shift;

  my %components;

  #### Parse off the directory
  my @pathParts = split(/\//,$filePath);
  my $directory = join("\/",@pathParts[0..($#pathParts-1)]);
  my $entry = $pathParts[$#pathParts];

  #### Parse the filename into pieces
  my @parts = split(/\./,$entry);
  my $nParts = scalar(@parts);
  my $basename = '';
  my $extension = 'noExt';
  my $uncompressedExtension = 'noExt';
  my $isCompressed = 0;
  if ( $nParts == 1) {
    $basename = $entry;
  } else {
    $extension = $parts[$nParts-1];
    my %compressedExtensions = ( gz=>1, zip=>1, bz2=>1 );
    if ( $compressedExtensions{$extension} ) {
      $isCompressed = 1;
      if ( $nParts == 2 ) {
        $basename = $parts[0];
      } else {
        $uncompressedExtension = $parts[$nParts-2];
        $basename = join(".",@parts[0..$nParts-3]);
      }
    } else {
      $uncompressedExtension = $extension;
      $basename = join(".",@parts[0..$nParts-2]);
    }
  }

  my $components = { directory=>$directory, basename=>$basename, extension=>$extension, uncompressedExtension=>$uncompressedExtension,
    isCompressed=>$isCompressed, filename=>$entry };

  print "DEBUG: Exiting $CLASS.$METHOD\n" if ( $DEBUG );
  return $components;
}


sub setBuiltinSignatureAttributeDescriptions {
###############################################################################
# setBuiltinSignatureAttributeDescriptions
###############################################################################
  my $METHOD = 'setBuiltinSignatureAttributeDescriptions';
  print "DEBUG: Entering $CLASS.$METHOD\n" if ( $DEBUG );
  my $self = shift || die ("self not passed");

  my $qckb = $self->getQckb();
  return if ( exists($qckb->{signatureInfo}->{"extrinsic.size"}->{friendlyName}) );

  my $info = $qckb->{signatureInfo};

  $info->{"extrinsic.size"}->{friendlyName} = "file size";
  $info->{"extrinsic.size"}->{sideName}->{upper} = "larger";
  $info->{"extrinsic.size"}->{sideName}->{lower} = "smaller";

  $info->{"extrinsic.mtime"}->{friendlyName} = "modification timestamp";
  $info->{"extrinsic.mtime"}->{sideName}->{upper} = "later";
  $info->{"extrinsic.mtime"}->{sideName}->{lower} = "earlier";

  $info->{"extrinsic.mode"}->{friendlyName} = "file permissions";
  $info->{"extrinsic.mode"}->{sideName}->{upper} = "different";
  $info->{"extrinsic.mode"}->{sideName}->{lower} = "different";

  $info->{"extrinsic.filename"}->{friendlyName} = "file name itself";
  $info->{"extrinsic.filename"}->{sideName}->{upper} = "longer or has different characters";
  $info->{"extrinsic.filename"}->{sideName}->{lower} = "shorter or has different characters";

  $info->{"extrinsic.basename"}->{friendlyName} = "file folder name";
  $info->{"extrinsic.basename"}->{sideName}->{upper} = "longer or has different characters";
  $info->{"extrinsic.basename"}->{sideName}->{lower} = "shorter or has different characters";

  $info->{"extrinsic.isReadable"}->{friendlyName} = "ability to read the file";
  $info->{"extrinsic.isReadable"}->{sideName}->{upper} = "different";
  $info->{"extrinsic.isReadable"}->{sideName}->{lower} = "different";

  return;
}


